var exec = require('child_process').exec;

exports = module.exports = growl;
exports.version = '1.4.1'

function growl(msg, options, fn) { // msg contains the payload
  var args = ["growlnotify"];
  args.push('-m');
  args.push(JSON.stringify(msg).replace(/\\n/g, '\n'));
  console.error("*** here ***: " + args.join(' '));
  exec(args.join(' '), function(){});
  // exec(args[2], function(){});
};